tests = [
    ("(())", 0),
    ("()()", 0),
    ("))(((((", 3),
    ("())", -1),
    ("))(", -1),
    (")))", -3),
    (")())())", -3),
]


def parse(path):
    return sum([1 if i == "(" else -1 for i in path])


for test in tests:
    assert parse(test[0]) == test[1]

